        this.addFilter = function(filter_data){
					this.disconnectFilters();
					for(str in FREQUENCY_FILTER_LIST){
						FREQUENCY_FILTER_LIST[str] = 0;
					}
					var filter_value = parseFloat(filter_data['value']);
					var filter_type, filter_frequency;
					if(filter_value < -10){
						filter_type = "lowpass";
						filter_frequency = ((filter_value + 100) * (5000 - 26.0))/90 + 26.0;
					}
					else if(filter_value > 10){
						filter_type = "highpass";
						filter_frequency = ((filter_value - 10) * (8000 - 26.0))/90 + 26.0;
					}
					else {
						this.applyFilters();
						return 0;
					}

          frequency_filter = new Tone.Filter(filter_frequency, filter_type);
					if(filter_value >= 80){
						frequency_filter.gain = 2;
					}
          filter_tracks = filter_data['tracks'];
					if(filter_tracks.length > 0){
            for(var i=0; i<filter_tracks.length; i++){
              switch(filter_tracks[i]){
                  case 'ALL': filter_all_flag = 1; break;

                  default: FREQUENCY_FILTER_LIST[TRACK_LIST[filter_tracks[i]]] = 1;
                            break;
              }
            }
          }
          if(filter_all_flag){
						filter_all_flag = 0;
						for(str in FILTER_LIST){
							FREQUENCY_FILTER_LIST[str] = 1;
	          }
          }
					this.applyFilters();
					return 0;
        }

      this.addStereoPan = function(panner_data){
				this.disconnectFilters();
				for(str in STEREOPAN_FILTER_LIST){
					STEREOPAN_FILTER_LIST[str] = 0;
				}
        var panner_tracks = panner_data['tracks'];
        var signal = parseInt(panner_data['value'])/100;
        stereopan_filter = new Tone.Panner(signal);
        if(panner_tracks.length > 0){
          for(var i=0; i<panner_tracks.length; i++){
            switch(panner_tracks[i]){
                case "ALL": panner_all_flag = 1; break;

                default: STEREOPAN_FILTER_LIST[TRACK_LIST[panner_tracks[i]]] = 1;
                          break;
            }
          }
        }
        if(panner_all_flag){
					panner_all_flag = 0;
          for(str in STEREOPAN_FILTER_LIST){
						STEREOPAN_FILTER_LIST[str] = 1;
          }
        }
				this.applyFilters();
				return 0;
			}

	this.applyFilters = function(){
		for(str in FILTER_LIST){
			var len = Object.keys(FILTER_LIST[str]).length;
			player[str].connect(FILTER_LIST[str][0]);
			for(var i=0; i< len-1; i++){
				FILTER_LIST[str][i].connect(FILTER_LIST[str][i+1]);
			}
			var last_filter = FILTER_LIST[str][len-1];
			if(FREQUENCY_FILTER_LIST[str] == 1){
				last_filter.connect(frequency_filter);
				last_filter = frequency_filter;
			}
			if(STEREOPAN_FILTER_LIST[str] == 1){
				last_filter.connect(stereopan_filter);
				last_filter = stereopan_filter;
			}
			last_filter.toMaster();
		}
	};

	this.disconnectFilters = function(){
		for(str in FILTER_LIST){
			var len = Object.keys(FILTER_LIST[str]).length;
			player[str].disconnect(FILTER_LIST[str][0]);
			for(var i=0; i< len-1; i++){
				FILTER_LIST[str][i].disconnect(FILTER_LIST[str][i+1]);
			}
			var last_filter = FILTER_LIST[str][len-1];
			if(FREQUENCY_FILTER_LIST[str] == 1){
				last_filter.disconnect(frequency_filter);
				last_filter = frequency_filter;
			}
			if(STEREOPAN_FILTER_LIST[str] == 1){
				last_filter.disconnect(stereopan_filter);
				last_filter = stereopan_filter;
			}
		}
	}

